import React, { useState, useContext, useEffect } from 'react';
import { GlobalContext } from "../context/GlobalState";
import { Link, useHistory } from "react-router-dom";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button
} from "reactstrap";

export const EditUser = (props) => {
  //const { editUser, users } = useContext(GlobalContext);

  const [selectedEmployee, setSelectedEmployee] = useState({
    id: '',
    employee_name: '',
    employee_age: '',
    employee_salary: ''
  })

  const [name, setName] = useState('');
  const [salary, setSalary] = useState('');
  const [age, setAge] = useState('');

  const [currentEmployeeId, setCurrentEmployeeId] = useState('');
  const [currentEmployeeName, setCurrentEmployeeName] = useState('');
  const [currentEmployeeSalary, setCurrentEmployeeSalary] = useState('');

  setCurrentEmployeeId(props.match.params.id);

  setCurrentEmployeeName(props.match.params.name);

  setCurrentEmployeeSalary(props.match.params.salary);

  console.log(props.match.params.id)

  console.log(props.match.params.name)

  console.log(props.match.params.salary)


  const history = useHistory();

  useEffect(() => {
    //const employee = currentEmployee;
    //const selectedUser = users.find(user => user.id === userId);
    //setSelectedEmployee(employee);
  }, [])

  const onChangeName = (e) => {
    setName(e.target.value)
  }

  const onChangeSalary = (e) => {
    setSalary(e.target.value)
  }

  const onChangeAge = (e) => {
    setAge(e.target.value)
  }

  const onSubmit = (e) => {
    e.preventDefault();
    editEmployee();
    history.push("/")
  }
  function editEmployee() {
    alert(selectedEmployee.id);
    alert(name);
    alert(salary);
    alert(age);
  }
  return (
    <Form onSubmit={onSubmit}>
      <FormGroup>
        <Label>Name</Label>
        <Input type="text"  value={currentEmployeeName} onChange={onChangeName} name="name" placeholder="Enter Name" required></Input>
        <Input type="text"  value={setCurrentEmployeeSalary} onChange={onChangeSalary} name="name" placeholder="Enter Salary" required></Input>
        <Input type="text"  onChange={onChangeAge} name="name" placeholder="Enter Age" required></Input>
      </FormGroup>
      <Button type="submit">Edit Name</Button>
      <Link to="/" className="btn btn-danger ml-2">Cancel</Link>
    </Form>
  )
}
