import React, { useState, useContext, useEffect } from 'react';
import { GlobalContext } from "../context/GlobalState";
import { Link, useHistory } from "react-router-dom";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button
} from "reactstrap";

export const EditUser = (props) => {

  const { users } = useContext(GlobalContext);

  const [selectedEmployee, setSelectedEmployee] = useState({
    id: '',
    employee_name: '',
    employee_salary: '',
    employee_age: "",
    profile_image: ''
  })

  const [name, setName] = useState('');
  const [salary, setSalary] = useState('');
  const [age, setAge] = useState('');

  const currentUserId = props.match.params.id;

  const history = useHistory();

  useEffect(() => {
    const userId = currentUserId;
    const selectedUser = users.find(user => user.id === currentUserId);
    setSelectedEmployee(selectedUser);
  }, [currentUserId, users])

  const onChangeName = (e) => {
    setName(e.target.value)
  }

  const onChangeSalary = (e) => {
    setSalary(e.target.value)
  }

  const onChangeAge = (e) => {
    setAge(e.target.value)
  }

  const onSubmit = (e) => {
    e.preventDefault();
    editEmployee();
    history.push("/")
  }
  function editEmployee() {
    alert(selectedEmployee.id);
    alert(name);
    alert(salary);
    alert(age);
  }
  return (
    <Form onSubmit={onSubmit}>
      <FormGroup>
        <Label>Name</Label>
        <Input type="text"    onChange={onChangeName} name="name" placeholder="Enter Name" required></Input>
        <Input type="text"    onChange={onChangeSalary} name="salary" placeholder="Enter Salary" required></Input>
        <Input type="text"    onChange={onChangeAge} name="age" placeholder="Enter Age" required></Input>
      </FormGroup>
      <Button type="submit">Edit Name</Button>
      <Link to="/" className="btn btn-danger ml-2">Cancel</Link>
    </Form>
  )
}
