import React, { useContext } from 'react';
import { Link } from "react-router-dom";
import {
  ListGroup,
  ListGroupItem,
  Button
} from "reactstrap";

export const UserList = props => {

  function deleteEmployee(id) {
    alert(id);
  }
  return (
    <ListGroup className="mt-4">
      {props.employees.length > 0 ? (
        <>
          {props.employees.map(employee => (
            <ListGroupItem className="d-flex" key={employee.id}>
              <strong>{employee.employee_name}</strong>
              <div className="ml-auto">
                <Link color="warning" className="btn btn-warning mr-1">Edit</Link>
                <Button onClick={deleteEmployee} color="danger">Delete</Button>
              </div>
            </ListGroupItem>
          ))}
        </>
      ) : (
          <h4 className="text-center">No Employees</h4>
        )}
    </ListGroup>
  )
}
