        <Input type="text"  value={selectedEmployee.employee_salary} onChange={onChangeSalary} name="salary" placeholder="Enter Salary" required></Input>
        <Input type="text"  value={selectedEmployee.employee_age} onChange={onChangeAge} name="age" placeholder="Enter Age" required></Input>
      </FormGroup>
      <Button type="submit">Edit Name</Button>
      <Link to="/" className="btn btn-danger ml-2">Cancel</Link>
